mapper.update:
update board
set
	board_title = #{boardTitle},
	board_content = #{boardContent}
where board_no = #{boardNo}

mapper.insert:
insert into board_comment values(
	seq_board_comment.nextval
	, #{boardCommentLevel}
	, #{boardCommentWriter}
	, #{boardCommentContent}
	, #{boardRef}
	, #{boardCommentRef}
	, sysdate
	, #{boardCommentPw}
)

insert into board values(
	board_seq.nextval
	, #{boardTitle}
	, #{boardContent}
	, #{boardWriter}
	, sysdate
	, #{boardPw}
	, #{boardRef}
	, #{filename}
	, #{filepath}
	)
